import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";function heroAnimation(){let t=gsap.timeline({scrollTrigger:{trigger:"#section-1 h1",start:"top 51%",end:"center 40%",scrub:!0,markers:!1}});return t.from("#section-1 h1, #section-1 h2",{duration:1,alpha:0,y:100}),t}function aboutAnimation(){let t=gsap.timeline({scrollTrigger:{trigger:"#section-2",start:"top 51%",end:"center 40%",scrub:!0,markers:!1}});return t.from("#section-2 h1",{duration:1,alpha:0,x:100}),t}function aboutinfoAnimation(){let t=gsap.timeline({scrollTrigger:{trigger:"#section-2 h1",start:"top 51%",end:"center 40%",scrub:!0,markers:!1}});return t.from("#section-2 p",{duration:1,alpha:0,x:-100}),t}function contacttxtAnimation(){let t=gsap.timeline({scrollTrigger:{trigger:"#section-3",start:"top 51%",end:"80% 80%%",scrub:!0,markers:!1}});return t.to("#section-3 h2, #section-3 p",{rotation:15,duration:.5,ease:"power1.inOut",yoyo:!0,repeat:5}),t}gsap.registerPlugin(ScrollTrigger),gsap.timeline();let mainTl=gsap.timeline();mainTl.add(heroAnimation()).add(aboutAnimation()).add(aboutinfoAnimation()).add(contacttxtAnimation);